mapper.select:
/* com.mtf.edumarine.mapper.EduMarineMapper.selectBoardNoticeList */ SELECT LISTB.* FROM (SELECT @ROWNUM := @ROWNUM + 1 AS rownum , LISTA.* FROM ( SELECT @ROWNUM := 0) R , ( SELECT seq , notice_gbn , title , writer , write_date , view_cnt , ( SELECT COUNT(*) FROM notice WHERE del_yn = 'N' AND title LIKE CONCAT('%',#{searchText},'%')
AND title LIKE CONCAT('%',#{searchText},'%')
AND content LIKE CONCAT('%',#{searchText},'%')
AND ( title LIKE CONCAT('%',#{searchText},'%') OR content LIKE CONCAT('%',#{searchText},'%') )
) AS totalRecords FROM notice WHERE del_yn = 'N'
AND title LIKE CONCAT('%',#{searchText},'%')
AND content LIKE CONCAT('%',#{searchText},'%')
AND ( title LIKE CONCAT('%',#{searchText},'%') OR content LIKE CONCAT('%',#{searchText},'%') )
) LISTA ORDER BY LISTA.notice_gbn asc, LISTA.write_date asc ) LISTB ORDER BY LISTB.rownum desc
LIMIT #{pageNum} , #{rows}

mapper.update:
/* com.mtf.edumarine.mapper.EduMarineMapper.updateBoardNoticeViewCnt */
UPDATE notice
SET view_cnt = view_cnt + 1
WHERE seq = #{value}

mapper.select:
/* com.mtf.edumarine.mapper.EduMarineMapper.selectBoardPressList */ SELECT LISTB.* FROM (SELECT @ROWNUM := @ROWNUM + 1 AS rownum , LISTA.* FROM ( SELECT @ROWNUM := 0) R , ( SELECT seq , notice_gbn , title , writer , write_date , view_cnt , ( SELECT COUNT(*) FROM press WHERE del_yn = 'N' AND title LIKE CONCAT('%',#{searchText},'%')
AND title LIKE CONCAT('%',#{searchText},'%')
AND content LIKE CONCAT('%',#{searchText},'%')
AND ( title LIKE CONCAT('%',#{searchText},'%') OR content LIKE CONCAT('%',#{searchText},'%') )
) AS totalRecords FROM press WHERE del_yn = 'N'
AND title LIKE CONCAT('%',#{searchText},'%')
AND content LIKE CONCAT('%',#{searchText},'%')
AND ( title LIKE CONCAT('%',#{searchText},'%') OR content LIKE CONCAT('%',#{searchText},'%') )
) LISTA ORDER BY LISTA.notice_gbn asc, LISTA.write_date asc ) LISTB ORDER BY LISTB.rownum desc
LIMIT #{pageNum} , #{rows}

mapper.update:
/* com.mtf.edumarine.mapper.EduMarineMapper.updateBoardPressViewCnt */
UPDATE press
SET view_cnt = view_cnt + 1
WHERE seq = #{value}

mapper.select:
/* com.mtf.edumarine.mapper.EduMarineMapper.selectBoardGalleryList */ SELECT LISTB.* FROM (SELECT @ROWNUM := @ROWNUM + 1 AS rownum , LISTA.* FROM ( SELECT @ROWNUM := 0) R , ( SELECT seq , title , writer , write_date , view_cnt , ( SELECT COUNT(*) FROM gallery WHERE del_yn = 'N' AND title LIKE CONCAT('%',#{searchText},'%')
AND title LIKE CONCAT('%',#{searchText},'%')
) AS totalRecords FROM gallery WHERE del_yn = 'N'
AND title LIKE CONCAT('%',#{searchText},'%')
) LISTA ORDER BY LISTA.write_date asc ) LISTB ORDER BY LISTB.rownum desc
LIMIT #{pageNum} , #{rows}

/* com.mtf.edumarine.mapper.EduMarineMapper.selectBoardMediaList */ SELECT seq , title , writer , write_date , media_url , media_key , ( SELECT COUNT(*) FROM media WHERE del_yn = 'N'
AND title LIKE CONCAT('%',#{searchText},'%')
) AS totalRecords FROM media WHERE del_yn = 'N'
AND title LIKE CONCAT('%',#{searchText},'%')
ORDER BY write_date DESC, final_regi_dttm DESC
LIMIT #{pageNum} , #{rows}

/* com.mtf.edumarine.mapper.EduMarineMapper.selectBoardNewsList */ SELECT seq , title , write_date , content , ( SELECT COUNT(*) FROM newsletter WHERE del_yn = 'N'
AND title LIKE CONCAT('%',#{searchText},'%')
) AS totalRecords FROM newsletter WHERE del_yn = 'N'
AND title LIKE CONCAT('%',#{searchText},'%')
ORDER BY write_date DESC, final_regi_dttm DESC
LIMIT #{pageNum} , #{rows}

