mapper.select:
/* com.mtf.edumarine.mapper.EduMarineMngMapper.selectTrainList */ SELECT seq , gbn , train_start_dttm , train_end_dttm , apply_start_dttm , apply_end_dttm , pay_sum , train_cnt , train_apply_cnt , train_note , exposure_yn , schedule_exposure_yn , del_yn , note , init_regi_pic , init_regi_dttm , final_regi_pic , final_regi_dttm FROM train WHERE 1=1 AND del_yn = 'N'
AND gbn LIKE CONCAT('%',#{searchText},'%')
ORDER BY train_start_dttm desc, init_regi_dttm DESC

mapper.update:
/* com.mtf.edumarine.mapper.EduMarineMngMapper.updateTrain */
UPDATE train
SET gbn = #{gbn}
  , train_start_dttm = #{trainStartDttm}
  , train_end_dttm = #{trainEndDttm}
  , apply_start_dttm = #{applyStartDttm}
  , apply_end_dttm = #{applyEndDttm}
  , pay_sum = #{paySum}
  , train_cnt = #{trainCnt}
  , train_note = #{trainNote}
  , exposure_yn = #{exposureYn}
  , schedule_exposure_yn = #{scheduleExposureYn}
  , note = #{note}
  , final_regi_dttm = DATE_FORMAT(NOW(), '%Y-%m-%d %T')
WHERE seq = #{seq}

