mapper.select:
/* com.mtf.edumarine.mapper.EdumarineMapper.login */
SELECT id
FROM admin
WHERE id = #{id}
  AND password = #{password}

/* com.mtf.edumarine.mapper.EduMarineMngMapper.selectNoticeList */ SELECT seq , lang , notice_gbn , title , writer , write_date , final_regi_dttm , view_cnt FROM notice WHERE 1=1
AND title LIKE CONCAT('%',#{searchText},'%')
AND lang = #{condition}
ORDER BY notice_gbn desc, init_regi_dttm desc, title asc

mapper.update:
/* com.mtf.edumarine.mapper.EduMarineMngMapper.updateNotice */
UPDATE notice
SET lang = #{lang}
, notice_gbn = #{noticeGbn}
, title = #{title}
, content = #{content}
, writer = #{writer}
, write_date = #{writeDate}

    , file_id_list = #{fileIdList}

, final_regi_dttm = DATE_FORMAT(NOW(), '%Y-%m-%d %T')
WHERE seq = #{seq}

mapper.insert:
/* com.mtf.edumarine.mapper.EduMarineMngMapper.insertNotice */
INSERT INTO notice
( seq
, lang
, notice_gbn
, title
, content
, writer
, write_date
, file_id_list
, note
, init_regi_pic
, init_regi_dttm
, final_regi_pic
, final_regi_dttm
)
VALUES
    ( #{seq}
    , #{lang}
    , #{noticeGbn}
    , #{title}
    , #{content}
    , #{writer}
    , #{writeDate}
    , #{fileIdList}
    , #{note}
    , 'NOTICE'
    , DATE_FORMAT(NOW(), '%Y-%m-%d %T')
    , 'NOTICE'
    , DATE_FORMAT(NOW(), '%Y-%m-%d %T')
    )

mapper.select:
/* com.mtf.edumarine.mapper.EduMarineMngMapper.selectPressList */ SELECT seq , lang , notice_gbn , title , writer , write_date , final_regi_dttm , view_cnt FROM press WHERE 1=1
AND title LIKE CONCAT('%',#{searchText},'%')
AND lang = #{condition}
ORDER BY notice_gbn desc, init_regi_dttm desc, title asc

