/* com.mtf.edumarine.mapper.edumarineMapper.insertFileInfo */
INSERT INTO file
( id
, user_id
, full_file_path
, full_path
, folder_path
, full_file_name
, uuid
, file_name
, file_yn
, note
, init_regi_pic
, init_regi_dttm
, final_regi_pic
, final_regi_dttm
)
VALUES
    ( #{id}
    , #{userId}
    , #{fullFilePath}
    , #{fullPath}
    , #{folderPath}
    , #{fullFileName}
    , #{uuid}
    , #{fileName}
    , #{fileYn}
    , #{note}
    , 'FILE'
    , DATE_FORMAT(NOW(), '%Y-%m-%d %T')
    , 'FILE'
    , DATE_FORMAT(NOW(), '%Y-%m-%d %T')
    )

mapper.update:
/* com.mtf.edumarine.mapper.edumarineMapper.updateFileUseN */
UPDATE file
SET file_yn = 'N'
  , final_regi_dttm = DATE_FORMAT(NOW(), '%Y-%m-%d %T')
WHERE id = #{id}

/* com.mtf.edumarine.mapper.edumarineMapper.updateFileUserId */
UPDATE file
SET user_id = #{userId}
  , final_regi_dttm = DATE_FORMAT(NOW(), '%Y-%m-%d %T')
WHERE id = #{id}
